import React, {useState} from 'react'
import axios from 'axios';
import {connect} from 'react-redux';
import {UPDATE_USER} from '../../redux/types/userTypes';
import checkError from '../../My-tools/My-tools';
import './UpdateUser.css';

function UpdateUser(props) {

    const [userUpdate, setUser] = useState({
        fullName: '',
        userName: '',
        email: '',
        birthDate: '',
        phoneNumber: '',
        address: ''
    })

    const [message, setMessage] = useState('')

    const handleStateUpdate = (event) => {
        setUser({...userUpdate, [event.target.name]: event.target.type === "number" ? +event.target.value : event.target.value});
    };

    //---AUTH-----------

    /* let token = props.token
    let auth = {
        headers: {
          'Authorization': `Bearer ${token}` 
        }}; */

    // FUNCTIONS

    const submitUpdate = async () => {

        //Error handling
        setMessage('');

        let error = checkError(userUpdate)
        setMessage(error);

        if(error){
            return;
        }


        let body = {

            fullName: userUpdate.fullName,
            userName: userUpdate.userName,
            email: userUpdate.email,
            birthDate: userUpdate.birthDate,
            phoneNumber: userUpdate.phoneNumber,
            address: userUpdate.address
        }

        let response = await axios.put(`http://localhost:3002/users/${userUpdate._id}`, body);

        if (response) {
            props.dispatch({type: UPDATE_USER, payload: response.data});
            alert('Datos actualizados con Exito');
        } else {
            alert('Lo siento , no se pudo actualizar el registro, vuelve a intentarlo mas tarde');
        }


    }

    return (
        <div className="udpateUserComponent">
            <div className="header">
                <h2>Update profile</h2>
            </div>
            <div className="lineForm"></div>
            <div className="formModal">
                <input type='text' name='fullName' title='Full Name' lenght='30' onChange={handleStateUpdate}></input> 
                <input type='text' name='userName' title='User Name' lenght='30' onChange={handleStateUpdate}></input> 
                <input type='email' name='email' title='Email' lenght='30' onChange={handleStateUpdate}></input> 
                <input type='number' name='phoneNumber' title='Phone Number' lenght='12' onChange={handleStateUpdate}></input> 
                <input type='date' name='birthDate' title='Date of Birth' lenght='30' onChange={handleStateUpdate}></input> 
                <input type='text' name='address' title='Full Address' lenght='250' onChange={handleStateUpdate}></input> 
            </div>
            <div className="messageUpdate">{message}</div>
            <div className="submitUpdate">
                <button type='submit' name='submit' onClick={()=>submitUpdate()} title='Update Profile'></button>
            </div>
        </div>
    )
}
const mapStateToProps =state =>{
    return{
      user : state.user,
      token : state.token
    }
  };

export default connect(mapStateToProps)(UpdateUser);
