import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';

import './Cart.css';
import Header from '../../components/Header/Header';


const Cart = (props) => {

    const [listToBuy, setListToBuy] = useState([])
    const [totalPrice, setTotalPrice] = useState(0)

    useEffect( ()=>{

        const parseObject = JSON.parse(localStorage.getItem("productCart"))
        setListToBuy(parseObject)
        const getPrice = 
         listToBuy.map(product => {
            return product.price
        })
        setTotalPrice(getPrice)
        console.log(getPrice)
/*         const sumPrice = await getPrice.reduce((a, b) => a + b, 0)
        console.log(sumPrice)
        await setTotalPrice(sumPrice) */

    },[localStorage.getItem("productCart")]) 

    return (
        <div className="content-header-user-cart">
           <Header/>
           <div className='cart-container'>
               <div className="cart-user-container">
               <h3>Mi Pedido</h3>
               <ul className="list-container">{
                listToBuy.map( (product, key)=>{
                return (
                <li className="list" key={key}>
                    <p>{product.name}</p>
                     <div className="product-img-container">
                        <img className="product" src={product.posterUrl} alt="imagenes"/> 
                    </div>
                    <p>{product.price} $</p>

                </li>)
            })
        }</ul>
        {
            listToBuy.map((product)=>{
                return (

                    <p>{totalPrice} $</p>
                    )
            })}


        <button className="buy-btn">Comprar</button> 
               </div>

           </div>


        </div>
    )
}



export default connect()(Cart);
